mapper.select:
SELECT

FROM sys_role a

WHERE a.id = #{id}

SELECT
FROM sys_role a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.enname = #{enname}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')

mapper.insert:
INSERT INTO sys_role_menu(
    id,
    role_id,
    menu_id
) VALUES (
    #{id},
    #{roleId},
    #{menuId}
)

mapper.update:
UPDATE sys_role SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
